import React from 'react';
import { BookOpen } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import CourseCard from '../components/CourseCard';
import { useCourses } from '../context/CourseContext';

const MyCoursesPage = () => {
  const navigate = useNavigate();
  const { courses, savedCourses } = useCourses();
  
  const mySavedCourses = courses.filter(c => savedCourses.includes(c.id));

  return (
    <div className="min-h-screen bg-gray-900 pb-12">
      <div className="max-w-7xl mx-auto px-4 py-8">
        <h1 className="text-4xl font-bold text-white mb-8">My Courses</h1>

        {mySavedCourses.length === 0 ? (
          <div className="bg-gray-800 rounded-xl p-12 text-center">
            <BookOpen className="w-16 h-16 text-gray-600 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-white mb-2">
              No saved courses yet
            </h3>
            <p className="text-gray-400 mb-6">
              Start exploring and save courses to track your learning journey
            </p>
            <button 
              onClick={() => navigate('/')} 
              className="bg-purple-600 hover:bg-purple-700 text-white font-semibold px-6 py-3 rounded-lg transition"
            >
              Browse Courses
            </button>
          </div>
        ) : (
          <>
            <div className="mb-6">
              <p className="text-gray-400">
                You have {mySavedCourses.length} saved course{mySavedCourses.length !== 1 ? 's' : ''}
              </p>
            </div>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {mySavedCourses.map(course => (
                <CourseCard key={course.id} course={course} />
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default MyCoursesPage;
