import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

const CourseContext = createContext();

export const useCourses = () => useContext(CourseContext);

export const CourseProvider = ({ children }) => {
  const [courses, setCourses] = useState([]);
  const [savedCourses, setSavedCourses] = useState([]);
  const [pendingCourses, setPendingCourses] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(false);
  const { user, token } = useAuth();

  // Fetch all courses
  const fetchCourses = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/courses');
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    } finally {
      setLoading(false);
    }
  };

  // Fetch saved courses for user
  const fetchSavedCourses = async () => {
    if (!user || !token) return;
    
    try {
      const response = await axios.get('http://localhost:5000/api/courses/user/saved', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setSavedCourses(response.data.map(course => course._id));
    } catch (error) {
      console.error('Error fetching saved courses:', error);
    }
  };

  // Fetch pending courses (admin only)
  const fetchPendingCourses = async () => {
    if (!user || user.role !== 'admin') return;
    
    try {
      const response = await axios.get('http://localhost:5000/api/admin/pending-courses', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setPendingCourses(response.data);
    } catch (error) {
      console.error('Error fetching pending courses:', error);
    }
  };

  // Fetch admin stats
  const fetchStats = async () => {
    if (!user || user.role !== 'admin') return;
    
    try {
      const response = await axios.get('http://localhost:5000/api/admin/stats', {
        headers: { Authorization: `Bearer ${token}` }
      });
      setStats(response.data);
    } catch (Error) {
      console.error('Error fetching stats:', error);
    }
  };

  // Search courses
  const searchCourses = async (query, filters = {}) => {
    try {
      setLoading(true);
      const params = new URLSearchParams();
      if (query) params.append('q', query);
      Object.entries(filters).forEach(([key, value]) => {
        if (value) params.append(key, value);
      });
      
      const response = await axios.get(`http://localhost:5000/api/courses/search?${params}`);
      setCourses(response.data);
    } catch (error) {
      console.error('Error searching courses:', error);
    } finally {
      setLoading(false);
    }
  };

  // Get course by ID
  const getCourseById = async (id) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/courses/${id}`);
      return response.data.course;
    } catch (error) {
      console.error('Error fetching course:', error);
      return null;
    }
  };

  // Submit new course
  const submitCourse = async (courseData) => {
    try {
      const response = await axios.post('http://localhost:5000/api/courses', courseData, {
        headers: { Authorization: `Bearer ${token}` }
      });
      
      // Refresh courses list
      await fetchCourses();
      if (user.role === 'admin') {
        await fetchPendingCourses();
      }
      
      return { success: true, course: response.data };
    } catch (error) {
      console.error('Error submitting course:', error);
      return { 
        success: false, 
        error: error.response?.data?.error || 'Failed to submit course' 
      };
    }
  };

  // Toggle save course
  const toggleSaveCourse = async (courseId) => {
    try {
      const response = await axios.post(
        `http://localhost:5000/api/courses/${courseId}/save`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      if (response.data.success) {
        // Update local state
        if (response.data.saved) {
          setSavedCourses(prev => [...prev, courseId]);
        } else {
          setSavedCourses(prev => prev.filter(id => id !== courseId));
        }
      }
      
      return response.data;
    } catch (error) {
      console.error('Error toggling save:', error);
      return { success: false, error: error.response?.data?.error || 'Failed to save course' };
    }
  };

  // Submit review
  const submitReview = async (courseId, reviewData) => {
    try {
      const response = await axios.post(
        `http://localhost:5000/api/courses/${courseId}/reviews`,
        reviewData,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      return response.data;
    } catch (error) {
      console.error('Error submitting review:', error);
      return { 
        success: false, 
        error: error.response?.data?.error || 'Failed to submit review' 
      };
    }
  };

  // Admin actions
  const approveCourse = async (courseId) => {
    try {
      await axios.post(
        `http://localhost:5000/api/admin/courses/${courseId}/approve`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      await Promise.all([fetchCourses(), fetchPendingCourses(), fetchStats()]);
      return true;
    } catch (error) {
      console.error('Error approving course:', error);
      return false;
    }
  };

  const rejectCourse = async (courseId) => {
    try {
      await axios.post(
        `http://localhost:5000/api/admin/courses/${courseId}/reject`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      await Promise.all([fetchCourses(), fetchPendingCourses(), fetchStats()]);
      return true;
    } catch (error) {
      console.error('Error rejecting course:', error);
      return false;
    }
  };

  const deleteCourse = async (courseId) => {
    try {
      await axios.delete(
        `http://localhost:5000/api/admin/courses/${courseId}`,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      
      await Promise.all([fetchCourses(), fetchPendingCourses(), fetchStats()]);
      return true;
    } catch (error) {
      console.error('Error deleting course:', error);
      return false;
    }
  };

  // Refresh all data
  const refreshCourses = async () => {
    await Promise.all([
      fetchCourses(),
      fetchSavedCourses(),
      user?.role === 'admin' && fetchPendingCourses(),
      user?.role === 'admin' && fetchStats()
    ]);
  };

  useEffect(() => {
    if (user) {
      fetchCourses();
      fetchSavedCourses();
      if (user.role === 'admin') {
        fetchPendingCourses();
        fetchStats();
      }
    }
  }, [user, token]);

  const value = {
    courses,
    savedCourses,
    pendingCourses,
    stats,
    loading,
    fetchCourses,
    fetchSavedCourses,
    searchCourses,
    getCourseById,
    submitCourse,
    toggleSaveCourse,
    submitReview,
    approveCourse,
    rejectCourse,
    deleteCourse,
    refreshCourses
  };

  return (
    <CourseContext.Provider value={value}>
      {children}
    </CourseContext.Provider>
  );
};
