import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

const CourseContext = createContext();

export const useCourses = () => {
  const context = useContext(CourseContext);
  if (!context) {
    throw new Error('useCourses must be used within CourseProvider');
  }
  return context;
};

export const CourseProvider = ({ children }) => {
  const [courses, setCourses] = useState([]);
  const [pendingCourses, setPendingCourses] = useState([]);
  const [savedCourses, setSavedCourses] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState(null);
  
  const { user, token } = useAuth();

  useEffect(() => {
    if (token) {
      fetchCourses();
      fetchSavedCourses();
      if (user?.role === 'admin') {
        fetchPendingCourses();
        fetchAdminStats();
      }
    }
  }, [token, user]);

  const fetchCourses = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/courses');
      setCourses(response.data);
    } catch (error) {
      console.error('Error fetching courses:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchPendingCourses = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/admin/pending-courses');
      setPendingCourses(response.data);
      setNotifications(
        response.data.length > 0 
          ? [{ id: 1, message: `${response.data.length} courses pending approval`, type: 'pending' }] 
          : []
      );
    } catch (error) {
      console.error('Error fetching pending courses:', error);
    }
  };

  const fetchSavedCourses = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/courses/user/saved');
      setSavedCourses(response.data);
    } catch (error) {
      console.error('Error fetching saved courses:', error);
    }
  };

  const fetchAdminStats = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/admin/stats');
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const searchCourses = async (query) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/courses/search?q=${query}`);
      return response.data;
    } catch (error) {
      console.error('Error searching courses:', error);
      return [];
    }
  };

  const getCourseById = async (id) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/courses/${id}`);
      return response.data;
    } catch (error) {
      console.error('Error fetching course:', error);
      return null;
    }
  };

  const toggleSaveCourse = async (courseId) => {
    try {
      const response = await axios.post(`http://localhost:5000/api/courses/${courseId}/save`);
      
      if (response.data.success) {
        if (response.data.saved) {
          setSavedCourses(prev => [...prev, courseId]);
        } else {
          setSavedCourses(prev => prev.filter(id => id !== courseId));
        }
      }
    } catch (error) {
      console.error('Error saving course:', error);
    }
  };

  const approveCourse = async (courseId) => {
    try {
      await axios.post(`http://localhost:5000/api/admin/courses/${courseId}/approve`);
      
      // Move from pending to approved
      const course = pendingCourses.find(c => c._id === courseId);
      if (course) {
        setCourses(prev => [...prev, { ...course, status: 'approved' }]);
        setPendingCourses(prev => prev.filter(c => c._id !== courseId));
        setNotifications(prev => prev.filter(n => n.type !== 'pending'));
      }
      
      return true;
    } catch (error) {
      console.error('Error approving course:', error);
      return false;
    }
  };

  const rejectCourse = async (courseId) => {
    try {
      await axios.post(`http://localhost:5000/api/admin/courses/${courseId}/reject`);
      setPendingCourses(prev => prev.filter(c => c._id !== courseId));
      setNotifications(prev => prev.filter(n => n.type !== 'pending'));
      return true;
    } catch (error) {
      console.error('Error rejecting course:', error);
      return false;
    }
  };

  const deleteCourse = async (courseId) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/courses/${courseId}`);
      setCourses(prev => prev.filter(c => c._id !== courseId));
      return true;
    } catch (error) {
      console.error('Error deleting course:', error);
      return false;
    }
  };

  const submitCourse = async (courseData) => {
    try {
      const response = await axios.post('http://localhost:5000/api/courses', courseData);
      
      if (user?.role === 'admin') {
        // Admin submission is auto-approved
        setCourses(prev => [...prev, response.data]);
      } else {
        // User submission goes to pending
        setPendingCourses(prev => [...prev, response.data]);
        setNotifications(prev => [
          ...prev,
          { 
            id: Date.now(), 
            message: 'Course submitted for admin approval', 
            type: 'info' 
          }
        ]);
      }
      
      return { success: true, course: response.data };
    } catch (error) {
      return { 
        success: false, 
        error: error.response?.data?.error || 'Failed to submit course' 
      };
    }
  };

  const submitReview = async (courseId, reviewData) => {
    try {
      const response = await axios.post(
        `http://localhost:5000/api/courses/${courseId}/reviews`,
        reviewData
      );
      
      if (response.data.success) {
        // Update course with new ratings
        const updatedCourses = courses.map(course => {
          if (course._id === courseId) {
            // Find course and update its rating
            const reviews = response.data.reviews;
            const totalReviews = reviews.length;
            const avgRating = reviews.reduce((sum, r) => sum + r.rating, 0) / totalReviews;
            
            return {
              ...course,
              rating: parseFloat(avgRating.toFixed(1)),
              reviews: totalReviews
            };
          }
          return course;
        });
        
        setCourses(updatedCourses);
      }
      
      return response.data;
    } catch (error) {
      console.error('Error submitting review:', error);
      return { success: false, error: error.response?.data?.error };
    }
  };

  const refreshCourses = () => {
    fetchCourses();
    if (user?.role === 'admin') {
      fetchPendingCourses();
      fetchAdminStats();
    }
  };

  const value = {
    courses,
    pendingCourses,
    savedCourses,
    notifications,
    loading,
    stats,
    searchCourses,
    getCourseById,
    toggleSaveCourse,
    approveCourse,
    rejectCourse,
    deleteCourse,
    submitCourse,
    submitReview,
    refreshCourses
  };

  return (
    <CourseContext.Provider value={value}>
      {children}
    </CourseContext.Provider>
  );
};
