import React, { useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { Star, Check, Clock, BookOpen, Award, Play, Users } from 'lucide-react';
import { useCourses } from '../context/CourseContext';

const CourseDetailPage = () => {
  const { id } = useParams();
  const navigate = useNavigate();
  const { courses, savedCourses, toggleSaveCourse } = useCourses();
  
  const course = courses.find(c => c.id === parseInt(id));
  const isSaved = savedCourses.includes(course?.id);
  
  const [activeTab, setActiveTab] = useState('overview');
  const [userReview, setUserReview] = useState({
    rating: 5,
    text: '',
    contentQuality: 5,
    instructorQuality: 5,
    valueForMoney: 5
  });

  if (!course) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <h2 className="text-2xl font-bold text-white mb-4">Course not found</h2>
          <button 
            onClick={() => navigate('/')}
            className="bg-purple-600 hover:bg-purple-700 text-white px-6 py-3 rounded-lg"
          >
            Back to Home
          </button>
        </div>
      </div>
    );
  }

  const handleReviewSubmit = () => {
    alert('Review submitted successfully!');
    setUserReview({
      rating: 5,
      text: '',
      contentQuality: 5,
      instructorQuality: 5,
      valueForMoney: 5
    });
  };

  return (
    <div className="min-h-screen bg-gray-900 pb-12">
      <div className="max-w-7xl mx-auto px-4 py-8">
        <button 
          onClick={() => navigate('/')} 
          className="text-purple-500 hover:text-purple-400 mb-6 flex items-center space-x-2"
        >
          <span>←</span>
          <span>Back to courses</span>
        </button>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Main Content */}
          <div className="lg:col-span-2">
            <div className="bg-gray-800 rounded-xl overflow-hidden mb-6">
              <img 
                src={course.thumbnail} 
                alt={course.title} 
                className="w-full h-96 object-cover" 
              />
            </div>

            <h1 className="text-4xl font-bold text-white mb-4">{course.title}</h1>
            <p className="text-xl text-gray-300 mb-6">{course.description}</p>

            {/* Instructor Info */}
            <div className="flex items-center space-x-4 mb-6">
              <div className="w-16 h-16 bg-purple-600 rounded-full flex items-center justify-center text-white text-xl font-bold">
                {course.instructor.charAt(0)}
              </div>
              <div>
                <div className="text-sm text-gray-400">Instructor</div>
                <div className="text-lg font-semibold text-white">{course.instructor}</div>
              </div>
            </div>

            {/* Tabs */}
            <div className="border-b border-gray-700 mb-6">
              <div className="flex space-x-8">
                {['overview', 'reviews', 'syllabus'].map(tab => (
                  <button
                    key={tab}
                    onClick={() => setActiveTab(tab)}
                    className={`pb-4 text-sm font-medium capitalize ${
                      activeTab === tab 
                        ? 'text-purple-500 border-b-2 border-purple-500' 
                        : 'text-gray-400 hover:text-white'
                    }`}
                  >
                    {tab}
                  </button>
                ))}
              </div>
            </div>

            {/* Tab Content */}
            {activeTab === 'overview' && (
              <div className="bg-gray-800 rounded-xl p-6">
                <h3 className="text-2xl font-bold text-white mb-4">What you'll learn</h3>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4 mb-6">
                  {course.skills?.map((skill, idx) => (
                    <div key={idx} className="flex items-start space-x-3">
                      <Check className="w-5 h-5 text-green-500 flex-shrink-0 mt-0.5" />
                      <span className="text-gray-300">{skill}</span>
                    </div>
                  ))}
                </div>

                <h3 className="text-2xl font-bold text-white mb-4 mt-8">Course Details</h3>
                <div className="grid grid-cols-2 gap-4">
                  <div className="flex items-center space-x-3 text-gray-300">
                    <Clock className="w-5 h-5 text-purple-500" />
                    <span>{course.duration}</span>
                  </div>
                  <div className="flex items-center space-x-3 text-gray-300">
                    <Users className="w-5 h-5 text-purple-500" />
                    <span>{course.level}</span>
                  </div>
                  <div className="flex items-center space-x-3 text-gray-300">
                    <BookOpen className="w-5 h-5 text-purple-500" />
                    <span>{course.reviews?.toLocaleString()} students</span>
                  </div>
                  <div className="flex items-center space-x-3 text-gray-300">
                    <Award className="w-5 h-5 text-purple-500" />
                    <span>{course.provider}</span>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'reviews' && (
              <div className="space-y-6">
                {/* Rating Breakdown */}
                <div className="bg-gray-800 rounded-xl p-6">
                  <h3 className="text-2xl font-bold text-white mb-6">Student Feedback</h3>
                  
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-8 mb-8">
                    {/* Overall Rating */}
                    <div className="text-center">
                      <div className="text-6xl font-bold text-white mb-2">
                        {course.rating}
                      </div>
                      <div className="flex justify-center mb-2">
                        {[1,2,3,4,5].map(star => (
                          <Star 
                            key={star} 
                            className={`w-6 h-6 ${
                              star <= Math.round(course.rating) 
                                ? 'fill-yellow-400 text-yellow-400' 
                                : 'text-gray-600'
                            }`} 
                          />
                        ))}
                      </div>
                      <div className="text-gray-400">
                        {course.reviews?.toLocaleString()} ratings
                      </div>
                    </div>

                    {/* Rating Distribution */}
                    <div className="space-y-3">
                      {[5,4,3,2,1].map(stars => (
                        <div key={stars} className="flex items-center space-x-3">
                          <span className="text-sm text-gray-400 w-12">
                            {stars} stars
                          </span>
                          <div className="flex-1 h-3 bg-gray-700 rounded-full overflow-hidden">
                            <div 
                              className="h-full bg-yellow-400 rounded-full transition-all"
                              style={{ width: `${course.ratingBreakdown?.[stars] || 0}%` }}
                            />
                          </div>
                          <span className="text-sm text-gray-400 w-12 text-right">
                            {course.ratingBreakdown?.[stars] || 0}%
                          </span>
                        </div>
                      ))}
                    </div>
                  </div>

                  {/* Aspect Ratings */}
                  <div className="grid grid-cols-1 md:grid-cols-3 gap-6 pt-6 border-t border-gray-700">
                    <div>
                      <div className="text-sm text-gray-400 mb-2">Content Quality</div>
                      <div className="flex items-center space-x-2">
                        <div className="flex-1 h-2 bg-gray-700 rounded-full overflow-hidden">
                          <div 
                            className="h-full bg-green-500 rounded-full" 
                            style={{width: `${(course.contentQuality/5)*100}%`}} 
                          />
                        </div>
                        <span className="text-white font-semibold">
                          {course.contentQuality}
                        </span>
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-400 mb-2">Instructor Quality</div>
                      <div className="flex items-center space-x-2">
                        <div className="flex-1 h-2 bg-gray-700 rounded-full overflow-hidden">
                          <div 
                            className="h-full bg-blue-500 rounded-full" 
                            style={{width: `${(course.instructorQuality/5)*100}%`}} 
                          />
                        </div>
                        <span className="text-white font-semibold">
                          {course.instructorQuality}
                        </span>
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-400 mb-2">Value for Money</div>
                      <div className="flex items-center space-x-2">
                        <div className="flex-1 h-2 bg-gray-700 rounded-full overflow-hidden">
                          <div 
                            className="h-full bg-purple-500 rounded-full" 
                            style={{width: `${(course.valueForMoney/5)*100}%`}} 
                          />
                        </div>
                        <span className="text-white font-semibold">
                          {course.valueForMoney}
                        </span>
                      </div>
                    </div>
                  </div>
                </div>

                {/* Write Review */}
                <div className="bg-gray-800 rounded-xl p-6">
                  <h3 className="text-xl font-bold text-white mb-4">Write a Review</h3>
                  <div className="space-y-4">
                    <div>
                      <label className="text-sm text-gray-400 mb-2 block">
                        Overall Rating
                      </label>
                      <div className="flex space-x-2">
                        {[1,2,3,4,5].map(star => (
                          <button 
                            key={star} 
                            onClick={() => setUserReview({...userReview, rating: star})}
                          >
                            <Star 
                              className={`w-8 h-8 cursor-pointer ${
                                star <= userReview.rating 
                                  ? 'fill-yellow-400 text-yellow-400' 
                                  : 'text-gray-600'
                              }`} 
                            />
                          </button>
                        ))}
                      </div>
                    </div>

                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      <div>
                        <label className="text-sm text-gray-400 mb-2 block">
                          Content Quality
                        </label>
                        <select 
                          value={userReview.contentQuality}
                          onChange={(e) => setUserReview({
                            ...userReview, 
                            contentQuality: Number(e.target.value)
                          })}
                          className="w-full bg-gray-700 text-white rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-purple-500"
                        >
                          {[1,2,3,4,5].map(n => (
                            <option key={n} value={n}>{n} Star{n>1?'s':''}</option>
                          ))}
                        </select>
                      </div>
                      <div>
                        <label className="text-sm text-gray-400 mb-2 block">
                          Instructor Quality
                        </label>
                        <select 
                          value={userReview.instructorQuality}
                          onChange={(e) => setUserReview({
                            ...userReview, 
                            instructorQuality: Number(e.target.value)
                          })}
                          className="w-full bg-gray-700 text-white rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-purple-500"
                        >
                          {[1,2,3,4,5].map(n => (
                            <option key={n} value={n}>{n} Star{n>1?'s':''}</option>
                          ))}
                        </select>
                      </div>
                      <div>
                        <label className="text-sm text-gray-400 mb-2 block">
                          Value for Money
                        </label>
                        <select 
                          value={userReview.valueForMoney}
                          onChange={(e) => setUserReview({
                            ...userReview, 
                            valueForMoney: Number(e.target.value)
                          })}
                          className="w-full bg-gray-700 text-white rounded-lg px-3 py-2 focus:outline-none focus:ring-2 focus:ring-purple-500"
                        >
                          {[1,2,3,4,5].map(n => (
                            <option key={n} value={n}>{n} Star{n>1?'s':''}</option>
                          ))}
                        </select>
                      </div>
                    </div>

                    <textarea
                      value={userReview.text}
                      onChange={(e) => setUserReview({...userReview, text: e.target.value})}
                      placeholder="Share your experience with this course..."
                      className="w-full bg-gray-700 text-white rounded-lg px-4 py-3 focus:outline-none focus:ring-2 focus:ring-purple-500 min-h-32"
                    />

                    <button 
                      onClick={handleReviewSubmit}
                      className="bg-purple-600 hover:bg-purple-700 text-white font-semibold px-6 py-3 rounded-lg transition"
                    >
                      Submit Review
                    </button>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'syllabus' && (
              <div className="bg-gray-800 rounded-xl p-6">
                <h3 className="text-2xl font-bold text-white mb-6">Course Curriculum</h3>
                <div className="space-y-4">
                  {[
                    'Introduction to the Course',
                    'Core Concepts',
                    'Advanced Topics',
                    'Real-world Projects',
                    'Final Assessment'
                  ].map((section, idx) => (
                    <div key={idx} className="bg-gray-700 rounded-lg p-4">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center space-x-3">
                          <div className="w-8 h-8 bg-purple-600 rounded-full flex items-center justify-center text-white text-sm font-bold">
                            {idx + 1}
                          </div>
                          <span className="text-white font-medium">{section}</span>
                        </div>
                        <span className="text-gray-400 text-sm">{3 + idx} lectures</span>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>

          {/* Sidebar */}
          <div className="lg:col-span-1">
            <div className="bg-gray-800 rounded-xl p-6 sticky top-20">
              <div className="text-4xl font-bold text-white mb-4">
                ${course.price}
              </div>
              
              <button className="w-full bg-purple-600 hover:bg-purple-700 text-white font-semibold py-4 rounded-lg mb-3 transition">
                Enroll Now on {course.provider}
              </button>
              
              <button 
                onClick={() => toggleSaveCourse(course.id)}
                className={`w-full border-2 font-semibold py-4 rounded-lg transition ${
                  isSaved
                    ? 'border-red-500 text-red-500 hover:bg-red-500 hover:text-white'
                    : 'border-purple-500 text-purple-500 hover:bg-purple-500 hover:text-white'
                }`}
              >
                {isSaved ? 'Saved ❤️' : 'Save Course'}
              </button>

              <div className="mt-6 pt-6 border-t border-gray-700 space-y-4">
                <h4 className="font-semibold text-white">This course includes:</h4>
                <div className="space-y-3 text-sm text-gray-300">
                  <div className="flex items-center space-x-3">
                    <Clock className="w-5 h-5 text-purple-500" />
                    <span>{course.duration} on-demand video</span>
                  </div>
                  <div className="flex items-center space-x-3">
                    <BookOpen className="w-5 h-5 text-purple-500" />
                    <span>Downloadable resources</span>
                  </div>
                  <div className="flex items-center space-x-3">
                    <Award className="w-5 h-5 text-purple-500" />
                    <span>Certificate of completion</span>
                  </div>
                  <div className="flex items-center space-x-3">
                    <Play className="w-5 h-5 text-purple-500" />
                    <span>Full lifetime access</span>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CourseDetailPage;
