  }, []);

  const toggleSaveCourse = (courseId) => {
    let updated;
    if (savedCourses.includes(courseId)) {
      updated = savedCourses.filter(id => id !== courseId);
    } else {
      updated = [...savedCourses, courseId];
    }
    setSavedCourses(updated);
    localStorage.setItem('savedCourses', JSON.stringify(updated));
