import React, { useState } from 'react';
import { X, Upload, DollarSign, Clock, BookOpen, Star } from 'lucide-react';
import { useCourses } from '../context/CourseContext';
import { useAuth } from '../context/AuthContext';

const AddCourseModal = ({ onClose }) => {
  const { submitCourse } = useCourses();
  const { user } = useAuth();
  
  const [formData, setFormData] = useState({
    title: '',
    instructor: '',
    provider: '',
    price: '',
    duration: '',
    level: 'Beginner',
    description: '',
    thumbnail: '',
    tags: '',
    category: '',
    url: '',
    skills: '',
    initialRating: 5,
    contentQuality: 5,
    instructorQuality: 5,
    valueForMoney: 5
  });

  const [errors, setErrors] = useState({});
  const [loading, setLoading] = useState(false);
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
    
    // Clear error for this field
    if (errors[name]) {
      setErrors(prev => ({ ...prev, [name]: '' }));
    }
  };

  const validateForm = () => {
    const newErrors = {};
    
    // Required fields
    if (!formData.title.trim()) newErrors.title = 'Course title is required';
    if (!formData.instructor.trim()) newErrors.instructor = 'Instructor name is required';
    if (!formData.provider.trim()) newErrors.provider = 'Provider is required';
    if (!formData.price || formData.price <= 0) newErrors.price = 'Valid price is required';
    if (!formData.duration.trim()) newErrors.duration = 'Duration is required';
    if (!formData.description.trim()) newErrors.description = 'Description is required';
    if (!formData.category.trim()) newErrors.category = 'Category is required';
    if (!formData.tags.trim()) newErrors.tags = 'Tags are required';
    
    // Rating validation
    if (formData.initialRating < 1 || formData.initialRating > 5) {
      newErrors.initialRating = 'Rating must be between 1-5';
    }
    
    return newErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setErrors({});
    setSuccess('');

    const formErrors = validateForm();
    
    if (Object.keys(formErrors).length > 0) {
      setErrors(formErrors);
      setLoading(false);
      return;
    }

    try {
      const courseData = {
        ...formData,
        price: Number(formData.price),
        tags: formData.tags.split(',').map(t => t.trim()).filter(t => t),
        skills: formData.skills ? formData.skills.split(',').map(s => s.trim()).filter(s => s) : [],
        category: formData.category.trim(),
        rating: Number(formData.initialRating),
        contentQuality: Number(formData.contentQuality),
        instructorQuality: Number(formData.instructorQuality),
        valueForMoney: Number(formData.valueForMoney),
        submittedBy: user._id || user.id,
        ratingBreakdown: {
          5: formData.initialRating === 5 ? 100 : 0,
          4: formData.initialRating === 4 ? 100 : 0,
          3: formData.initialRating === 3 ? 100 : 0,
          2: formData.initialRating === 2 ? 100 : 0,
          1: formData.initialRating === 1 ? 100 : 0
        }
      };

      const result = await submitCourse(courseData);
      
      if (result.success) {
        setSuccess('Course submitted successfully! It will be reviewed by admin.');
        
        // Reset form
        setFormData({
          title: '',
          instructor: '',
          provider: '',
          price: '',
          duration: '',
          level: 'Beginner',
          description: '',
          thumbnail: '',
          tags: '',
          category: '',
          url: '',
          skills: '',
          initialRating: 5,
          contentQuality: 5,
          instructorQuality: 5,
          valueForMoney: 5
        });
        
        // Auto-close after 2 seconds for admin, stay open for users
        if (user.role === 'admin') {
          setTimeout(() => {
            onClose();
          }, 2000);
        }
      } else {
        setErrors({ submit: result.error || 'Failed to submit course' });
      }
    } catch (error) {
      setErrors({ submit: error.message || 'Something went wrong' });
    } finally {
      setLoading(false);
    }
  };

  const categories = [
    'Web Development', 'Data Science', 'Machine Learning', 'Cloud Computing',
    'Mobile Development', 'Design', 'Marketing', 'Business', 'Finance',
    'Health & Fitness', 'Music', 'Photography', 'Language', 'Other'
  ];

  return (
    <div className="fixed inset-0 bg-black bg-opacity-75 flex items-center justify-center z-50 p-4 overflow-y-auto">
      <div className="bg-gray-800 rounded-xl max-w-4xl w-full my-8 max-h-[90vh] overflow-y-auto">
        <div className="sticky top-0 bg-gray-800 p-6 border-b border-gray-700 flex items-center justify-between z-10">
          <h2 className="text-2xl font-bold text-white">Submit New Course</h2>
          <button 
            onClick={onClose} 
            className="text-gray-400 hover:text-white transition"
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        <form onSubmit={handleSubmit} className="p-6 space-y-6">
          {success && (
            <div className="bg-green-900 bg-opacity-50 border border-green-700 text-green-200 px-4 py-3 rounded-lg">
              {success}
            </div>
          )}
          
          {errors.submit && (
            <div className="bg-red-900 bg-opacity-50 border border-red-700 text-red-200 px-4 py-3 rounded-lg">
              {errors.submit}
            </div>
          )}
          
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            {/* Left Column - Basic Info */}
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Course Title *
                </label>
                <input
                  type="text"
                  name="title"
                  value={formData.title}
                  onChange={handleChange}
                  placeholder="e.g., Complete Python Bootcamp"
                  className={`w-full px-4 py-3 bg-gray-700 border ${
                    errors.title ? 'border-red-500' : 'border-gray-600'
                  } rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500`}
                  disabled={loading}
                />
                {errors.title && <p className="text-red-400 text-sm mt-1">{errors.title}</p>}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Instructor Name *
                </label>
                <input
                  type="text"
                  name="instructor"
                  value={formData.instructor}
                  onChange={handleChange}
                  placeholder="e.g., Dr. Angela Yu"
                  className={`w-full px-4 py-3 bg-gray-700 border ${
                    errors.instructor ? 'border-red-500' : 'border-gray-600'
                  } rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-purple-500`}
                  disabled={loading}
                />
                {errors.instructor && <p className="text-red-400 text-sm mt-1">{errors.instructor}</p>}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Provider/Platform *
                </label>
                <select
                  name="provider"
                  value={formData.provider}
                  onChange={handleChange}
                  className={`w-full px-4 py-3 bg-gray-700 border ${
                    errors.provider ? 'border-red-500' : 'border-gray-600'
                  } rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-purple-500`}
                  disabled={loading}
                >
                  <option value="">Select Provider</option>
                  <option value="Udemy">Udemy</option>
                  <option value="Coursera">Coursera</option>
                  <option value="edX">edX</option>
                  <option value="Pluralsight">Pluralsight</option>
                  <option value="LinkedIn Learning">LinkedIn Learning</option>
                  <option value="Skillshare">Skillshare</option>
                  <option value="YouTube">YouTube</option>
                  <option value="Other">Other</option>
                </select>
                {errors.provider && <p className="text-red-400 text-sm mt-1">{errors.provider}</p>}
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-2">
                    Price (USD) *
                  </label>
                  <div className="relative">
                    <DollarSign className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                    <input
                      type="number"
                      name="price"
                      value={formData.price}
                      onChange={handleChange}
                      placeholder="99.99"
                      step="0.01"
                      min="0"
                      className={`w-full pl-10 pr-4 py-3 bg-gray-700 border ${
                        errors.price ? 'border-red-500' : 'border-gray-600'
                      } rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-purple-500`}
                      disabled={loading}
                    />
                  </div>
                  {errors.price && <p className="text-red-400 text-sm mt-1">{errors.price}</p>}
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-2">
                    Duration *
                  </label>
                  <div className="relative">
                    <Clock className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                    <input
                      type="text"
                      name="duration"
                      value={formData.duration}
                      onChange={handleChange}
                      placeholder="65 hours"
                      className={`w-full pl-10 pr-4 py-3 bg-gray-700 border ${
                        errors.duration ? 'border-red-500' : 'border-gray-600'
                      } rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-purple-500`}
                      disabled={loading}
                    />
                  </div>
                  {errors.duration && <p className="text-red-400 text-sm mt-1">{errors.duration}</p>}
                </div>
              </div>

              <div className="grid grid-cols-2 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-2">
                    Level *
                  </label>
                  <select
                    name="level"
                    value={formData.level}
                    onChange={handleChange}
                    className="w-full px-4 py-3 bg-gray-700 border border-gray-600 rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-purple-500"
                    disabled={loading}
                  >
                    <option value="Beginner">Beginner</option>
                    <option value="Intermediate">Intermediate</option>
                    <option value="Advanced">Advanced</option>
                  </select>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-300 mb-2">
                    Category *
                  </label>
                  <select
                    name="category"
                    value={formData.category}
                    onChange={handleChange}
                    className={`w-full px-4 py-3 bg-gray-700 border ${
                      errors.category ? 'border-red-500' : 'border-gray-600'
                    } rounded-lg text-white focus:outline-none focus:ring-2 focus:ring-purple-500`}
                    disabled={loading}
                  >
                    <option value="">Select Category</option>
                    {categories.map(cat => (
                      <option key={cat} value={cat}>{cat}</option>
                    ))}
                  </select>
                  {errors.category && <p className="text-red-400 text-sm mt-1">{errors.category}</p>}
                </div>
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Course URL (Optional)
                </label>
                <input
                  type="url"
                  name="url"
                  value={formData.url}
                  onChange={handleChange}
                  placeholder="https://www.udemy.com/course/..."
                  className="w-full px-4 py-3 bg-gray-700 border border-gray-600 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500"
                  disabled={loading}
                />
              </div>
            </div>

            {/* Right Column - Details & Ratings */}
            <div className="space-y-4">
              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Description *
                </label>
                <textarea
                  name="description"
                  value={formData.description}
                  onChange={handleChange}
                  placeholder="Describe what students will learn, course content, target audience..."
                  className={`w-full px-4 py-3 bg-gray-700 border ${
                    errors.description ? 'border-red-500' : 'border-gray-600'
                  } rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 min-h-32`}
                  disabled={loading}
                  rows={6}
                />
                {errors.description && <p className="text-red-400 text-sm mt-1">{errors.description}</p>}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Tags (comma-separated) *
                </label>
                <input
                  type="text"
                  name="tags"
                  value={formData.tags}
                  onChange={handleChange}
                  placeholder="Python, Web Development, Data Science, Machine Learning"
                  className={`w-full px-4 py-3 bg-gray-700 border ${
                    errors.tags ? 'border-red-500' : 'border-gray-600'
                  } rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500`}
                  disabled={loading}
                />
                {errors.tags && <p className="text-red-400 text-sm mt-1">{errors.tags}</p>}
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Skills Learned (comma-separated)
                </label>
                <input
                  type="text"
                  name="skills"
                  value={formData.skills}
                  onChange={handleChange}
                  placeholder="React, Node.js, MongoDB, Express, REST API"
                  className="w-full px-4 py-3 bg-gray-700 border border-gray-600 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500"
                  disabled={loading}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-300 mb-2">
                  Thumbnail URL (Optional)
                </label>
                <div className="relative">
                  <Upload className="absolute left-3 top-1/2 transform -translate-y-1/2 w-5 h-5 text-gray-400" />
                  <input
                    type="url"
                    name="thumbnail"
                    value={formData.thumbnail}
                    onChange={handleChange}
                    placeholder="https://example.com/image.jpg (Leave empty for auto-assigned)"
                    className="w-full pl-10 pr-4 py-3 bg-gray-700 border border-gray-600 rounded-lg text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500"
                    disabled={loading}
                  />
                </div>
                <p className="text-xs text-gray-400 mt-1">
                  Leave empty for automatic thumbnail based on category
                </p>
              </div>

              {/* Rating Section */}
              <div className="bg-gray-750 rounded-lg p-4">
                <h3 className="text-lg font-semibold text-white mb-4 flex items-center">
                  <Star className="w-5 h-5 text-yellow-500 mr-2" />
                  Initial Course Rating *
                </h3>
                
                <div className="space-y-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-300 mb-2">
                      Overall Rating (1-5)
                    </label>
                    <div className="flex items-center space-x-2">
                      {[1, 2, 3, 4, 5].map(star => (
                        <button
                          key={star}
                          type="button"
                          onClick={() => handleChange({ target: { name: 'initialRating', value: star } })}
                          className="text-2xl"
                        >
                          <Star 
                            className={`w-8 h-8 cursor-pointer ${
                              star <= formData.initialRating 
                                ? 'fill-yellow-500 text-yellow-500' 
                                : 'text-gray-600'
                            }`} 
                          />
                        </button>
                      ))}
                      <span className="text-white font-bold ml-2">{formData.initialRating}/5</span>
                    </div>
                    {errors.initialRating && <p className="text-red-400 text-sm mt-1">{errors.initialRating}</p>}
                  </div>

                  <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                    <div>
                      <label className="block text-sm text-gray-300 mb-2">Content Quality</label>
                      <select
                        name="contentQuality"
                        value={formData.contentQuality}
                        onChange={handleChange}
                        className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white text-sm"
                      >
                        {[1,2,3,4,5].map(n => <option key={n} value={n}>{n} Star{n>1?'s':''}</option>)}
                      </select>
                    </div>
                    <div>
                      <label className="block text-sm text-gray-300 mb-2">Instructor Quality</label>
                      <select
                        name="instructorQuality"
                        value={formData.instructorQuality}
                        onChange={handleChange}
                        className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white text-sm"
                      >
                        {[1,2,3,4,5].map(n => <option key={n} value={n}>{n} Star{n>1?'s':''}</option>)}
                      </select>
                    </div>
                    <div>
                      <label className="block text-sm text-gray-300 mb-2">Value for Money</label>
                      <select
                        name="valueForMoney"
                        value={formData.valueForMoney}
                        onChange={handleChange}
                        className="w-full px-3 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white text-sm"
                      >
                        {[1,2,3,4,5].map(n => <option key={n} value={n}>{n} Star{n>1?'s':''}</option>)}
                      </select>
                    </div>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div className="flex space-x-4 pt-6 border-t border-gray-700">
            <button
              type="submit"
              disabled={loading}
              className="flex-1 bg-purple-600 hover:bg-purple-700 text-white font-semibold py-3 rounded-lg transition disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center"
            >
              {loading ? (
                <>
                  <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                    <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                    <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                  </svg>
                  Submitting...
                </>
              ) : (
                'Submit Course for Approval'
              )}
            </button>
            <button
              type="button"
              onClick={onClose}
              disabled={loading}
              className="flex-1 bg-gray-700 hover:bg-gray-600 text-white font-semibold py-3 rounded-lg transition disabled:opacity-50"
            >
              Cancel
            </button>
          </div>

          <div className="text-center">
            <p className="text-sm text-gray-400">
              {user.role === 'admin' 
                ? 'Your course will be published immediately as you are an admin.'
                : 'Your course will be reviewed by an admin before being published.'}
            </p>
          </div>
        </form>
      </div>
    </div>
  );
};

export default AddCourseModal;
