import React, { useState } from 'react';
import { Search, BookOpen, Users, Award, TrendingUp } from 'lucide-react';
import CourseCard from '../components/CourseCard';
import { useCourses } from '../context/CourseContext';

const HomePage = () => {
  const { courses } = useCourses();
  const [searchQuery, setSearchQuery] = useState('');

  const filteredCourses = courses.filter(course => 
    course.title.toLowerCase().includes(searchQuery.toLowerCase()) ||
    course.tags?.some(tag => tag.toLowerCase().includes(searchQuery.toLowerCase())) ||
    course.instructor.toLowerCase().includes(searchQuery.toLowerCase())
  );

  const popularTags = ['Web Development', 'Data Science', 'Design', 'Marketing', 'Cloud'];

  return (
    <div className="min-h-screen bg-gray-900 pb-12">
      {/* Hero Section */}
      <div className="relative bg-gradient-to-r from-purple-900 to-indigo-900 py-20 px-4">
        <div className="max-w-7xl mx-auto">
          <h1 className="text-5xl md:text-6xl font-bold text-white mb-4">
            Learn Without Limits
          </h1>
          <p className="text-xl text-gray-300 mb-8 max-w-2xl">
            Discover courses from top platforms. Compare, track, and master your skills.
          </p>
          
          {/* Search Bar */}
          <div className="relative max-w-3xl">
            <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
            <input
              type="text"
              placeholder="Search for courses..."
              value={searchQuery}
              onChange={(e) => setSearchQuery(e.target.value)}
              className="w-full pl-12 pr-4 py-4 bg-white rounded-xl text-gray-900 placeholder-gray-500 focus:outline-none focus:ring-2 focus:ring-purple-500 text-lg"
            />
          </div>
          
          {/* Quick Filters */}
          <div className="mt-6 flex flex-wrap gap-3">
            {popularTags.map(tag => (
              <button 
                key={tag} 
                onClick={() => setSearchQuery(tag)} 
                className="bg-gray-800 hover:bg-gray-700 text-white px-4 py-2 rounded-full text-sm transition"
              >
                {tag}
              </button>
            ))}
          </div>
        </div>
      </div>

      {/* Stats Section */}
      <div className="max-w-7xl mx-auto px-4 -mt-8">
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <div className="bg-gray-800 rounded-lg p-6 text-center">
            <BookOpen className="w-8 h-8 text-purple-500 mx-auto mb-2" />
            <div className="text-3xl font-bold text-white">{courses.length}</div>
            <div className="text-sm text-gray-400">Courses Available</div>
          </div>
          <div className="bg-gray-800 rounded-lg p-6 text-center">
            <Users className="w-8 h-8 text-blue-500 mx-auto mb-2" />
            <div className="text-3xl font-bold text-white">50K+</div>
            <div className="text-sm text-gray-400">Active Learners</div>
          </div>
          <div className="bg-gray-800 rounded-lg p-6 text-center">
            <Award className="w-8 h-8 text-yellow-500 mx-auto mb-2" />
            <div className="text-3xl font-bold text-white">15K+</div>
            <div className="text-sm text-gray-400">Certificates Earned</div>
          </div>
          <div className="bg-gray-800 rounded-lg p-6 text-center">
            <TrendingUp className="w-8 h-8 text-green-500 mx-auto mb-2" />
            <div className="text-3xl font-bold text-white">4.6â˜…</div>
            <div className="text-sm text-gray-400">Average Rating</div>
          </div>
        </div>
      </div>

      {/* Trending Section */}
      <div className="max-w-7xl mx-auto px-4 mt-12">
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-3xl font-bold text-white">
            {searchQuery ? 'Search Results' : 'Trending Now'}
          </h2>
          {!searchQuery && (
            <button className="text-purple-500 hover:text-purple-400 text-sm font-medium">
              View All â†’
            </button>
          )}
        </div>
        
        {filteredCourses.length === 0 ? (
          <div className="bg-gray-800 rounded-xl p-12 text-center">
            <p className="text-gray-400 text-lg">No courses found matching "{searchQuery}"</p>
          </div>
        ) : (
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {filteredCourses.map(course => (
              <CourseCard key={course.id} course={course} />
            ))}
          </div>
        )}
      </div>

      {/* Categories Section */}
      <div className="max-w-7xl mx-auto px-4 mt-16">
        <h2 className="text-3xl font-bold text-white mb-6">Popular Categories</h2>
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {[
            { name: 'Development', icon: 'ðŸ’»', count: 245 },
            { name: 'Business', icon: 'ðŸ’¼', count: 189 },
            { name: 'Design', icon: 'ðŸŽ¨', count: 167 },
            { name: 'Marketing', icon: 'ðŸ“ˆ', count: 143 },
            { name: 'IT & Software', icon: 'ðŸ”§', count: 198 },
            { name: 'Photography', icon: 'ðŸ“·', count: 112 },
            { name: 'Music', icon: 'ðŸŽµ', count: 98 },
            { name: 'Health', icon: 'ðŸ’ª', count: 134 }
          ].map(cat => (
            <div 
              key={cat.name} 
              className="bg-gray-800 hover:bg-gray-700 rounded-xl p-6 cursor-pointer transition group"
            >
              <div className="text-4xl mb-3">{cat.icon}</div>
              <div className="text-lg font-semibold text-white group-hover:text-purple-400 transition">
                {cat.name}
              </div>
              <div className="text-sm text-gray-400">{cat.count} courses</div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HomePage;
