import React, { createContext, useState, useContext, useEffect } from 'react';
import axios from 'axios';
import { useAuth } from './AuthContext';

const CourseContext = createContext();

export const useCourses = () => {
  const context = useContext(CourseContext);
  if (!context) {
    throw new Error('useCourses must be used within CourseProvider');
  }
  return context;
};

export const CourseProvider = ({ children }) => {
  const [courses, setCourses] = useState([]);
  const [pendingCourses, setPendingCourses] = useState([]);
  const [savedCoursesIds, setSavedCoursesIds] = useState([]);
  const [savedCoursesData, setSavedCoursesData] = useState([]);
  const [notifications, setNotifications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState(null);
  
  const { user, token } = useAuth();

  useEffect(() => {
    if (token) {
      fetchCourses();
      fetchSavedCourses();
      if (user?.role === 'admin') {
        fetchPendingCourses();
        fetchAdminStats();
      }
    }
  }, [token, user]);

  const fetchCourses = async () => {
    try {
      setLoading(true);
      const response = await axios.get('http://localhost:5000/api/courses');
      const formattedCourses = response.data.map(course => ({
        ...course,
        thumbnail: course.thumbnail || `https://images.unsplash.com/photo-1516321318423-f06f85e504b3?w=400&auto=format&fit=crop`,
        rating: course.rating || 0,
        reviews: course.reviews || 0,
        ratingBreakdown: course.ratingBreakdown || {5:0,4:0,3:0,2:0,1:0}
      }));
      setCourses(formattedCourses);
    } catch (error) {
      console.error('Error fetching courses:', error);
    } finally {
      setLoading(false);
    }
  };

  const fetchPendingCourses = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/admin/pending-courses');
      setPendingCourses(response.data);
      
      // Update notifications
      if (response.data.length > 0) {
        setNotifications([{ 
          id: 1, 
          message: `${response.data.length} courses pending approval`, 
          type: 'pending' 
        }]);
      } else {
        setNotifications([]);
      }
    } catch (error) {
      console.error('Error fetching pending courses:', error);
    }
  };

  const fetchSavedCourses = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/courses/user/saved');
      const savedData = response.data.map(course => ({
        ...course,
        thumbnail: course.thumbnail || `https://images.unsplash.com/photo-1516321318423-f06f85e504b3?w=400&auto=format&fit=crop`,
        rating: course.rating || 0,
        reviews: course.reviews || 0
      }));
      setSavedCoursesData(savedData);
      
      // Extract just the IDs for quick lookup
      const savedIds = savedData.map(course => course._id);
      setSavedCoursesIds(savedIds);
    } catch (error) {
      console.error('Error fetching saved courses:', error);
    }
  };

  const fetchAdminStats = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/admin/stats');
      setStats(response.data);
    } catch (error) {
      console.error('Error fetching stats:', error);
    }
  };

  const searchCourses = async (query) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/courses/search?q=${query}`);
      return response.data;
    } catch (error) {
      console.error('Error searching courses:', error);
      return [];
    }
  };

  const getCourseById = async (id) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/courses/${id}`);
      if (response.data.course) {
        return {
          ...response.data.course,
          thumbnail: response.data.course.thumbnail || `https://images.unsplash.com/photo-1516321318423-f06f85e504b3?w=400&auto=format&fit=crop`,
          rating: response.data.course.rating || 0,
          reviews: response.data.course.reviews || 0,
          ratingBreakdown: response.data.course.ratingBreakdown || {5:0,4:0,3:0,2:0,1:0},
          contentQuality: response.data.course.contentQuality || 0,
          instructorQuality: response.data.course.instructorQuality || 0,
          valueForMoney: response.data.course.valueForMoney || 0
        };
      }
      return null;
    } catch (error) {
      console.error('Error fetching course:', error);
      return null;
    }
  };

  const toggleSaveCourse = async (courseId) => {
    try {
      const response = await axios.post(`http://localhost:5000/api/courses/${courseId}/save`);
      
      if (response.data.success) {
        if (response.data.saved) {
          // Add to saved
          setSavedCoursesIds(prev => [...prev, courseId]);
          
          // Find and add the course data
          const courseToSave = courses.find(c => c._id === courseId);
          if (courseToSave) {
            setSavedCoursesData(prev => [...prev, courseToSave]);
          }
        } else {
          // Remove from saved
          setSavedCoursesIds(prev => prev.filter(id => id !== courseId));
          setSavedCoursesData(prev => prev.filter(c => c._id !== courseId));
        }
        
        return { success: true, saved: response.data.saved };
      }
    } catch (error) {
      console.error('Error saving course:', error);
      return { success: false, error: error.response?.data?.error };
    }
  };

  const approveCourse = async (courseId) => {
    try {
      const response = await axios.post(`http://localhost:5000/api/admin/courses/${courseId}/approve`);
      
      // Move from pending to courses
      const course = pendingCourses.find(c => c._id === courseId);
      if (course) {
        const approvedCourse = {
          ...course,
          status: 'approved',
          thumbnail: course.thumbnail || `https://images.unsplash.com/photo-1516321318423-f06f85e504b3?w=400&auto=format&fit=crop`
        };
        
        setCourses(prev => [...prev, approvedCourse]);
        setPendingCourses(prev => prev.filter(c => c._id !== courseId));
        setNotifications(prev => prev.filter(n => n.type !== 'pending'));
      }
      
      return { success: true };
    } catch (error) {
      console.error('Error approving course:', error);
      return { success: false, error: error.response?.data?.error };
    }
  };

  const rejectCourse = async (courseId) => {
    try {
      await axios.post(`http://localhost:5000/api/admin/courses/${courseId}/reject`);
      setPendingCourses(prev => prev.filter(c => c._id !== courseId));
      setNotifications(prev => prev.filter(n => n.type !== 'pending'));
      return { success: true };
    } catch (error) {
      console.error('Error rejecting course:', error);
      return { success: false, error: error.response?.data?.error };
    }
  };

  const deleteCourse = async (courseId) => {
    try {
      await axios.delete(`http://localhost:5000/api/admin/courses/${courseId}`);
      setCourses(prev => prev.filter(c => c._id !== courseId));
      return { success: true };
    } catch (error) {
      console.error('Error deleting course:', error);
      return { success: false, error: error.response?.data?.error };
    }
  };

  const submitCourse = async (courseData) => {
    try {
      const response = await axios.post('http://localhost:5000/api/courses', courseData);
      
      const newCourse = {
        ...response.data,
        thumbnail: response.data.thumbnail || `https://images.unsplash.com/photo-1516321318423-f06f85e504b3?w=400&auto=format&fit=crop`,
        rating: response.data.rating || 0,
        reviews: response.data.reviews || 0
      };
      
      if (user?.role === 'admin') {
        // Admin submission - add to courses immediately
        setCourses(prev => [...prev, newCourse]);
      } else {
        // User submission - add to pending
        setPendingCourses(prev => [...prev, newCourse]);
        setNotifications(prev => [
          ...prev,
          { 
            id: Date.now(), 
            message: 'Course submitted for admin approval', 
            type: 'info' 
          }
        ]);
      }
      
      return { success: true, course: newCourse };
    } catch (error) {
      return { 
        success: false, 
        error: error.response?.data?.error || 'Failed to submit course' 
      };
    }
  };

  const submitReview = async (courseId, reviewData) => {
    try {
      const response = await axios.post(
        `http://localhost:5000/api/courses/${courseId}/reviews`,
        reviewData
      );
      
      if (response.data.success) {
        // Update the course in the courses list
        setCourses(prev => prev.map(course => {
          if (course._id === courseId) {
            return {
              ...course,
              rating: response.data.course.rating || course.rating,
              reviews: response.data.course.reviews || course.reviews,
              ratingBreakdown: response.data.course.ratingBreakdown || course.ratingBreakdown
            };
          }
          return course;
        }));
        
        // Update in saved courses if exists
        setSavedCoursesData(prev => prev.map(course => {
          if (course._id === courseId) {
            return {
              ...course,
              rating: response.data.course.rating || course.rating,
              reviews: response.data.course.reviews || course.reviews
            };
          }
          return course;
        }));
      }
      
      return response.data;
    } catch (error) {
      console.error('Error submitting review:', error);
      return { success: false, error: error.response?.data?.error };
    }
  };

  const refreshCourses = () => {
    fetchCourses();
    fetchSavedCourses();
    if (user?.role === 'admin') {
      fetchPendingCourses();
      fetchAdminStats();
    }
  };

  const value = {
    courses,
    pendingCourses,
    savedCourses: savedCoursesIds,
    savedCoursesData,
    notifications,
    loading,
    stats,
    searchCourses,
    getCourseById,
    toggleSaveCourse,
    approveCourse,
    rejectCourse,
    deleteCourse,
    submitCourse,
    submitReview,
    refreshCourses,
    fetchCourses,
    fetchSavedCourses
  };

  return (
    <CourseContext.Provider value={value}>
      {children}
    </CourseContext.Provider>
  );
};
