import React, { useState, useEffect } from 'react';
import { BookOpen, Heart, Clock, Star, User, ExternalLink } from 'lucide-react';
import { useNavigate } from 'react-router-dom';
import { useCourses } from '../context/CourseContext';
import { useAuth } from '../context/AuthContext';

const MyCoursesPage = () => {
  const navigate = useNavigate();
  const { savedCourses, courses } = useCourses();
  const { user } = useAuth();
  
  // Filter courses that are saved
  const mySavedCourses = courses.filter(course => 
    savedCourses.some(savedId => savedId.toString() === course._id.toString())
  );

  const handleCourseClick = (courseId) => {
    navigate(`/course/${courseId}`);
  };

  const handleEnrollClick = (e, url) => {
    e.stopPropagation();
    if (url) {
      window.open(url, '_blank', 'noopener,noreferrer');
    } else {
      alert('Course URL not available');
    }
  };

  return (
    <div className="min-h-screen bg-gray-900 pb-12">
      <div className="max-w-7xl mx-auto px-4 py-8">
        <h1 className="text-4xl font-bold text-white mb-2">My Learning</h1>
        <p className="text-gray-400 mb-8">Track your saved courses and learning progress</p>

        {mySavedCourses.length === 0 ? (
          <div className="bg-gray-800 rounded-xl p-12 text-center">
            <BookOpen className="w-16 h-16 text-gray-600 mx-auto mb-4" />
            <h3 className="text-xl font-semibold text-white mb-2">
              No saved courses yet
            </h3>
            <p className="text-gray-400 mb-6">
              Start exploring and save courses to track your learning journey
            </p>
            <button 
              onClick={() => navigate('/')} 
              className="bg-purple-600 hover:bg-purple-700 text-white font-semibold px-6 py-3 rounded-lg transition"
            >
              Browse Courses
            </button>
          </div>
        ) : (
          <>
            <div className="mb-6">
              <p className="text-gray-400">
                You have {mySavedCourses.length} saved course{mySavedCourses.length !== 1 ? 's' : ''}
              </p>
            </div>
            
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {mySavedCourses.map(course => (
                <div 
                  key={course._id}
                  className="bg-gray-800 rounded-xl overflow-hidden hover:ring-2 hover:ring-purple-500 transition cursor-pointer group"
                  onClick={() => handleCourseClick(course._id)}
                >
                  <div className="relative">
                    <img 
                      src={course.thumbnail} 
                      alt={course.title} 
                      className="w-full h-48 object-cover group-hover:scale-105 transition-transform duration-300"
                    />
                    <div className="absolute top-2 right-2">
                      <div className="bg-gray-900 bg-opacity-80 p-2 rounded-full">
                        <Heart className="w-5 h-5 fill-red-500 text-red-500" />
                      </div>
                    </div>
                    <div className="absolute top-2 left-2 bg-purple-600 px-3 py-1 rounded-full text-xs font-semibold text-white">
                      {course.provider}
                    </div>
                  </div>
                  
                  <div className="p-4">
                    <h3 className="text-lg font-semibold text-white mb-2 line-clamp-2">
                      {course.title}
                    </h3>
                    
                    <p className="text-sm text-gray-400 mb-3">{course.instructor}</p>
                    
                    <div className="flex items-center space-x-4 mb-3">
                      <div className="flex items-center space-x-1">
                        <Star className="w-4 h-4 fill-yellow-400 text-yellow-400" />
                        <span className="text-sm font-semibold text-white">{course.rating || 0}</span>
                        <span className="text-xs text-gray-400">
                          ({course.reviews?.toLocaleString() || 0})
                        </span>
                      </div>
                      <div className="flex items-center space-x-1 text-gray-400">
                        <Clock className="w-4 h-4" />
                        <span className="text-xs">{course.duration}</span>
                      </div>
                    </div>
                    
                    <div className="flex flex-wrap gap-2 mb-3">
                      {course.tags?.slice(0, 3).map((tag, idx) => (
                        <span 
                          key={idx} 
                          className="text-xs bg-gray-700 text-gray-300 px-2 py-1 rounded"
                        >
                          {tag}
                        </span>
                      ))}
                    </div>
                    
                    <div className="flex items-center justify-between">
                      <span className="text-2xl font-bold text-white">${course.price || 0}</span>
                      <button
                        onClick={(e) => handleEnrollClick(e, course.url)}
                        className="flex items-center space-x-2 bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg text-sm transition"
                      >
                        <ExternalLink className="w-4 h-4" />
                        <span>Enroll</span>
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </>
        )}
      </div>
    </div>
  );
};

export default MyCoursesPage;
