import React from 'react';
import { Check, Trash2, AlertCircle } from 'lucide-react';
import CourseCard from '../components/CourseCard';
import { useCourses } from '../context/CourseContext';

const AdminPage = () => {
  const { courses, pendingCourses, approveCourse, deleteCourse } = useCourses();

  return (
    <div className="min-h-screen bg-gray-900 pb-12">
      <div className="max-w-7xl mx-auto px-4 py-8">
        <div className="flex items-center justify-between mb-8">
          <h1 className="text-4xl font-bold text-white">Admin Dashboard</h1>
          <div className="flex items-center space-x-2 bg-purple-600 px-4 py-2 rounded-lg">
            <AlertCircle className="w-5 h-5 text-white" />
            <span className="text-white font-semibold">
              {pendingCourses.length} Pending
            </span>
          </div>
        </div>

        {/* Stats */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-6 mb-8">
          <div className="bg-gray-800 rounded-lg p-6">
            <div className="text-sm text-gray-400 mb-2">Total Courses</div>
            <div className="text-3xl font-bold text-white">{courses.length}</div>
          </div>
          <div className="bg-gray-800 rounded-lg p-6">
            <div className="text-sm text-gray-400 mb-2">Pending Approval</div>
            <div className="text-3xl font-bold text-yellow-500">
              {pendingCourses.length}
            </div>
          </div>
          <div className="bg-gray-800 rounded-lg p-6">
            <div className="text-sm text-gray-400 mb-2">Total Users</div>
            <div className="text-3xl font-bold text-white">1,234</div>
          </div>
          <div className="bg-gray-800 rounded-lg p-6">
            <div className="text-sm text-gray-400 mb-2">Total Reviews</div>
            <div className="text-3xl font-bold text-white">5,678</div>
          </div>
        </div>

        {/* Pending Courses */}
        <div className="bg-gray-800 rounded-xl p-6 mb-8">
          <h2 className="text-2xl font-bold text-white mb-6">
            Pending Course Approvals
          </h2>
          
          {pendingCourses.length === 0 ? (
            <div className="text-center py-12">
              <Check className="w-16 h-16 text-green-500 mx-auto mb-4" />
              <p className="text-gray-400">All caught up! No pending courses.</p>
            </div>
          ) : (
            <div className="space-y-4">
              {pendingCourses.map(course => (
                <div key={course.id} className="bg-gray-700 rounded-lg p-6">
                  <div className="flex flex-col lg:flex-row lg:items-center justify-between gap-4">
                    <div className="flex-1">
                      <h3 className="text-xl font-semibold text-white mb-2">
                        {course.title}
                      </h3>
                      <div className="grid grid-cols-2 gap-4 text-sm text-gray-300">
                        <div>
                          <span className="text-gray-400">Instructor:</span>{' '}
                          {course.instructor}
                        </div>
                        <div>
                          <span className="text-gray-400">Provider:</span>{' '}
                          {course.provider}
                        </div>
                        <div>
                          <span className="text-gray-400">Price:</span> ${course.price}
                        </div>
                        <div>
                          <span className="text-gray-400">Duration:</span>{' '}
                          {course.duration}
                        </div>
                        <div>
                          <span className="text-gray-400">Level:</span> {course.level}
                        </div>
                        <div>
                          <span className="text-gray-400">Submitted by:</span>{' '}
                          {course.submittedBy}
                        </div>
                      </div>
                    </div>
                    
                    <div className="flex flex-col space-y-3 lg:w-48">
                      <button 
                        onClick={() => approveCourse(course.id)}
                        className="bg-green-600 hover:bg-green-700 text-white font-semibold px-6 py-3 rounded-lg transition flex items-center justify-center space-x-2"
                      >
                        <Check className="w-5 h-5" />
                        <span>Approve</span>
                      </button>
                      <button 
                        onClick={() => {
                          if(window.confirm('Are you sure you want to reject this course?')) {
                            deleteCourse(course.id);
                          }
                        }}
                        className="bg-red-600 hover:bg-red-700 text-white font-semibold px-6 py-3 rounded-lg transition flex items-center justify-center space-x-2"
                      >
                        <Trash2 className="w-5 h-5" />
                        <span>Reject</span>
                      </button>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* All Courses */}
        <div className="bg-gray-800 rounded-xl p-6">
          <h2 className="text-2xl font-bold text-white mb-6">
            All Approved Courses
          </h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {courses.map(course => (
              <CourseCard key={course.id} course={course} />
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminPage;
