import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Plus, Bell, User, LogOut } from 'lucide-react';
import { useAuth } from '../context/AuthContext';
import { useCourses } from '../context/CourseContext';

const NavBar = ({ onAddCourse }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const { user, logout } = useAuth();
  const { notifications } = useCourses();

  const isActive = (path) => location.pathname === path;

  return (
    <nav className="bg-gray-900 border-b border-gray-800 sticky top-0 z-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex items-center justify-between h-16">
          <div className="flex items-center space-x-8">
            <h1 
              className="text-2xl font-bold text-purple-500 cursor-pointer" 
              onClick={() => navigate('/')}
            >
              CourseHub
            </h1>
            <div className="hidden md:flex space-x-4">
              <button 
                onClick={() => navigate('/')} 
                className={`px-3 py-2 rounded-md text-sm font-medium transition ${
                  isActive('/') 
                    ? 'text-white bg-gray-800' 
                    : 'text-gray-300 hover:text-white hover:bg-gray-800'
                }`}
              >
                Home
              </button>
              <button 
                onClick={() => navigate('/my-courses')} 
                className={`px-3 py-2 rounded-md text-sm font-medium transition ${
                  isActive('/my-courses') 
                    ? 'text-white bg-gray-800' 
                    : 'text-gray-300 hover:text-white hover:bg-gray-800'
                }`}
              >
                My Courses
              </button>
              {user?.role === 'admin' && (
                <button 
                  onClick={() => navigate('/admin')} 
                  className={`px-3 py-2 rounded-md text-sm font-medium transition ${
                    isActive('/admin') 
                      ? 'text-white bg-gray-800' 
                      : 'text-gray-300 hover:text-white hover:bg-gray-800'
                  }`}
                >
                  Admin
                </button>
              )}
            </div>
          </div>
          
          <div className="flex items-center space-x-4">
            <button 
              onClick={onAddCourse}
              className="bg-purple-600 hover:bg-purple-700 px-4 py-2 rounded-lg text-sm font-medium text-white flex items-center space-x-2 transition"
            >
              <Plus className="w-4 h-4" />
              <span className="hidden sm:inline">Add Course</span>
            </button>
            
            <button className="relative text-gray-300 hover:text-white transition">
              <Bell className="w-6 h-6" />
              {notifications.length > 0 && (
                <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs w-5 h-5 rounded-full flex items-center justify-center">
                  {notifications.length}
                </span>
              )}
            </button>
            
            <div className="relative group">
              <button className="flex items-center space-x-2 text-gray-300 hover:text-white transition">
                <User className="w-6 h-6" />
                <span className="hidden sm:inline text-sm">{user?.name}</span>
              </button>
              <div className="absolute right-0 mt-2 w-48 bg-gray-800 rounded-lg shadow-lg py-2 hidden group-hover:block">
                <button 
                  onClick={logout}
                  className="w-full text-left px-4 py-2 text-sm text-gray-300 hover:bg-gray-700 flex items-center space-x-2 transition"
                >
                  <LogOut className="w-4 h-4" />
                  <span>Logout</span>
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default NavBar;
