import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { Plus, Bell, User, LogOut, Menu, X, Home, BookOpen, Shield, CheckCircle } from 'lucide-react';
import { useAuth } from '../context/AuthContext';
import { useCourses } from '../context/CourseContext';
import axios from 'axios';

const NavBar = ({ onAddCourse }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const { user, logout } = useAuth();
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);
  const [userMenuOpen, setUserMenuOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [notificationsOpen, setNotificationsOpen] = useState(false);
  const [unreadCount, setUnreadCount] = useState(0);

  useEffect(() => {
    if (user) {
      fetchNotifications();
      // Poll for new notifications every 30 seconds
      const interval = setInterval(fetchNotifications, 30000);
      return () => clearInterval(interval);
    }
  }, [user]);

  const fetchNotifications = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/notifications');
      if (response.data.success) {
        setNotifications(response.data.notifications);
        const unread = response.data.notifications.filter(n => !n.read).length;
        setUnreadCount(unread);
      }
    } catch (error) {
      console.error('Error fetching notifications:', error);
    }
  };

  const markAsRead = async (notificationId) => {
    try {
      await axios.post(`http://localhost:5000/api/notifications/${notificationId}/read`);
      fetchNotifications();
    } catch (error) {
      console.error('Error marking notification as read:', error);
    }
  };

  const markAllAsRead = async () => {
    try {
      await axios.post('http://localhost:5000/api/notifications/read-all');
      fetchNotifications();
    } catch (error) {
      console.error('Error marking all as read:', error);
    }
  };

  const handleLogout = async () => {
    await logout();
    navigate('/login');
    setUserMenuOpen(false);
  };

  const isActive = (path) => location.pathname === path;

  const menuItems = [
    { path: '/', label: 'Home', icon: Home },
    { path: '/my-courses', label: 'My Courses', icon: BookOpen },
    ...(user?.role === 'admin' ? [{ path: '/admin', label: 'Admin', icon: Shield }] : [])
  ];

  return (
    <>
      <nav className="bg-gray-900 border-b border-gray-800 sticky top-0 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between h-16">
            <div className="flex items-center">
              <button
                onClick={() => setMobileMenuOpen(!mobileMenuOpen)}
                className="md:hidden text-gray-300 hover:text-white mr-3"
              >
                {mobileMenuOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
              </button>
              
              <h1 
                className="text-2xl font-bold text-purple-500 cursor-pointer" 
                onClick={() => navigate('/')}
              >
                CourseHub
              </h1>
              
              <div className="hidden md:flex ml-8 space-x-4">
                {menuItems.map((item) => (
                  <button 
                    key={item.path}
                    onClick={() => navigate(item.path)} 
                    className={`px-3 py-2 rounded-md text-sm font-medium transition flex items-center ${
                      isActive(item.path) 
                        ? 'text-white bg-gray-800' 
                        : 'text-gray-300 hover:text-white hover:bg-gray-800'
                    }`}
                  >
                    <item.icon className="w-4 h-4 mr-2" />
                    {item.label}
                  </button>
                ))}
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <button 
                onClick={onAddCourse}
                className="bg-purple-600 hover:bg-purple-700 px-4 py-2 rounded-lg text-sm font-medium text-white flex items-center space-x-2 transition"
              >
                <Plus className="w-4 h-4" />
                <span className="hidden sm:inline">Add Course</span>
              </button>
              
              <div className="relative">
                <button 
                  onClick={() => setNotificationsOpen(!notificationsOpen)}
                  className="relative text-gray-300 hover:text-white transition"
                >
                  <Bell className="w-6 h-6" />
                  {unreadCount > 0 && (
                    <span className="absolute -top-1 -right-1 bg-red-500 text-white text-xs w-5 h-5 rounded-full flex items-center justify-center">
                      {unreadCount}
                    </span>
                  )}
                </button>
                
                {notificationsOpen && (
                  <div className="absolute right-0 mt-2 w-96 bg-gray-800 rounded-lg shadow-lg py-2 z-50 border border-gray-700 max-h-96 overflow-y-auto">
                    <div className="px-4 py-3 border-b border-gray-700 flex justify-between items-center">
                      <h3 className="font-semibold text-white">Notifications</h3>
                      {unreadCount > 0 && (
                        <button 
                          onClick={markAllAsRead}
                          className="text-xs text-purple-400 hover:text-purple-300"
                        >
                          Mark all as read
                        </button>
                      )}
                    </div>
                    
                    {notifications.length === 0 ? (
                      <div className="px-4 py-8 text-center">
                        <Bell className="w-12 h-12 text-gray-600 mx-auto mb-3" />
                        <p className="text-gray-400">No notifications yet</p>
                      </div>
                    ) : (
                      <div className="divide-y divide-gray-700">
                        {notifications.map(notification => (
                          <div 
                            key={notification._id}
                            className={`px-4 py-3 hover:bg-gray-750 cursor-pointer ${!notification.read ? 'bg-gray-750' : ''}`}
                            onClick={() => {
                              markAsRead(notification._id);
                              if (notification.data?.courseId) {
                                navigate(`/course/${notification.data.courseId}`);
                              }
                              setNotificationsOpen(false);
                            }}
                          >
                            <div className="flex items-start">
                              <div className={`w-3 h-3 rounded-full mt-1 mr-3 ${
                                notification.type === 'course_approved' ? 'bg-green-500' :
                                notification.type === 'course_pending' ? 'bg-yellow-500' :
                                notification.type === 'course_rejected' ? 'bg-red-500' :
                                'bg-blue-500'
                              }`} />
                              <div className="flex-1">
                                <h4 className="text-sm font-medium text-white">{notification.title}</h4>
                                <p className="text-xs text-gray-400 mt-1">{notification.message}</p>
                                <p className="text-xs text-gray-500 mt-1">
                                  {new Date(notification.createdAt).toLocaleDateString()}
                                </p>
                              </div>
                              {!notification.read && (
                                <div className="w-2 h-2 bg-blue-500 rounded-full"></div>
                              )}
                            </div>
                          </div>
                        ))}
                      </div>
                    )}
                  </div>
                )}
              </div>
              
              <div className="relative">
                <button 
                  onClick={() => setUserMenuOpen(!userMenuOpen)}
                  className="flex items-center space-x-2 text-gray-300 hover:text-white transition"
                >
                  <div className="w-8 h-8 bg-purple-600 rounded-full flex items-center justify-center text-white text-sm font-bold">
                    {user?.name?.charAt(0) || 'U'}
                  </div>
                  <span className="hidden sm:inline text-sm">{user?.name}</span>
                  <span className={`text-xs px-2 py-1 rounded-full ${
                    user?.role === 'admin' 
                      ? 'bg-purple-900 text-purple-300' 
                      : 'bg-gray-700 text-gray-300'
                  }`}>
                    {user?.role}
                  </span>
                </button>
                
                {userMenuOpen && (
                  <div className="absolute right-0 mt-2 w-56 bg-gray-800 rounded-lg shadow-lg py-2 z-50 border border-gray-700">
                    <div className="px-4 py-3 border-b border-gray-700">
                      <p className="text-sm font-medium text-white">{user?.name}</p>
                      <p className="text-xs text-gray-400">{user?.email}</p>
                      <div className="mt-2 flex items-center">
                        <div className="flex-1 bg-gray-700 rounded-full h-2">
                          <div 
                            className="bg-yellow-500 h-2 rounded-full" 
                            style={{ width: `${Math.min(user?.xp || 0, 100)}%` }}
                          />
                        </div>
                        <span className="text-xs text-gray-400 ml-2">Level {Math.floor((user?.xp || 0) / 100) + 1}</span>
                      </div>
                    </div>
                    
                    <button 
                      onClick={() => {
                        navigate('/my-courses');
                        setUserMenuOpen(false);
                      }}
                      className="w-full text-left px-4 py-3 text-sm text-gray-300 hover:bg-gray-700 flex items-center"
                    >
                      <BookOpen className="w-4 h-4 mr-3" />
                      My Courses
                    </button>
                    
                    <button 
                      onClick={() => {
                        onAddCourse();
                        setUserMenuOpen(false);
                      }}
                      className="w-full text-left px-4 py-3 text-sm text-gray-300 hover:bg-gray-700 flex items-center"
                    >
                      <Plus className="w-4 h-4 mr-3" />
                      Add Course
                    </button>
                    
                    {user?.role === 'admin' && (
                      <button 
                        onClick={() => {
                          navigate('/admin');
                          setUserMenuOpen(false);
                        }}
                        className="w-full text-left px-4 py-3 text-sm text-gray-300 hover:bg-gray-700 flex items-center"
                      >
                        <Shield className="w-4 h-4 mr-3" />
                        Admin Panel
                      </button>
                    )}
                    
                    <div className="border-t border-gray-700 mt-2 pt-2">
                      <button 
                        onClick={handleLogout}
                        className="w-full text-left px-4 py-3 text-sm text-red-300 hover:bg-red-900 hover:text-red-100 flex items-center"
                      >
                        <LogOut className="w-4 h-4 mr-3" />
                        Logout
                      </button>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </nav>

      {/* Mobile Menu */}
      {mobileMenuOpen && (
        <div className="md:hidden bg-gray-800 border-b border-gray-700">
          <div className="px-4 py-3 space-y-1">
            {menuItems.map((item) => (
              <button 
                key={item.path}
                onClick={() => {
                  navigate(item.path);
                  setMobileMenuOpen(false);
                }}
                className={`w-full text-left px-3 py-3 rounded-md text-base font-medium flex items-center ${
                  isActive(item.path) 
                    ? 'text-white bg-gray-900' 
                    : 'text-gray-300 hover:text-white hover:bg-gray-700'
                }`}
              >
                <item.icon className="w-5 h-5 mr-3" />
                {item.label}
              </button>
            ))}
            
            <div className="border-t border-gray-700 pt-3 mt-3">
              <button 
                onClick={() => {
                  onAddCourse();
                  setMobileMenuOpen(false);
                }}
                className="w-full text-left px-3 py-3 rounded-md text-base font-medium text-gray-300 hover:text-white hover:bg-gray-700 flex items-center"
              >
                <Plus className="w-5 h-5 mr-3" />
                Add Course
              </button>
              
              <button 
                onClick={handleLogout}
                className="w-full text-left px-3 py-3 rounded-md text-base font-medium text-red-300 hover:text-red-100 hover:bg-red-900 flex items-center"
              >
                <LogOut className="w-5 h-5 mr-3" />
                Logout
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Close menus when clicking outside */}
      {(userMenuOpen || notificationsOpen) && (
        <div 
          className="fixed inset-0 z-40" 
          onClick={() => {
            setUserMenuOpen(false);
            setNotificationsOpen(false);
          }}
        />
      )}
    </>
  );
};

export default NavBar;
